<?phpnamespace MNGame\Service\ApiClient;use GuzzleHttp\Client;use GuzzleHttp\Exception\ClientException;use GuzzleHttp\Exception\GuzzleException;class RestApiClient{    public const GET = 'GET';    public const POST = 'POST';    private Client $client;    public function __construct()    {        $this->client = new Client(['timeout' => 10, 'verify' => false]);    }    /**     * @throws GuzzleException     */    public function request(string $method, string $url, $data = null): string    {        try {            return $this->client                ->request($method, $url, empty($data) ? [] : $data)                ->getBody()                ->getContents();        } catch (ClientException $e) {            return (string)$e->getResponse()->getBody();        }    }}